mapper.insert:

insert 
	into account 
	values(null, #{email}, #{password}, #{nickname}, #{phoneNumber}, #{name}, null, now(),null);

choose.when:

	update account
       set name=#{name },
           gender=#{gender }
     where id=${id }

choose.otherwise:

update account
   set name=#{name },
       password=#{password }, 
       gender=#{gender }
 where id=${id }

mapper.update:

update account
set nickname=#{nickname }
where no=${no }


update account
set password=#{password}
where no=${no }


update account
set name=#{name },
    phone_number=#{phoneNumber}
where id=${id }

mapper.select:

select no, email, nickname
  from account 
 where email = #{email } 
   and password = #{password }


select email, password
	from account 
	where name = #{name} 
	and email = #{email}


select email
	from account 
	where name = #{name} 
	and phone_number = #{phoneNumber}

mapper.update:

update account
    set password=#{randomPassword }
    where email=#{email }


update account 
set token= #{token}
where no = #{no}

mapper.select:

select no, email, nickname, phone_number, thumbnail_url
  from account 
 where no = #{no }
