mapper.insert:

insert 
	into account 
	values(null, #{email}, #{password}, #{nickname}, #{phoneNumber}, #{name}, null, now());

choose.when:

	update account
       set name=#{name },
           gender=#{gender }
     where id=${id }

choose.otherwise:

update account
   set name=#{name },
       password=#{password }, 
       gender=#{gender }
 where id=${id }

mapper.update:

update account
set nickname=#{nickname }
where no=${no }


update account
set password=#{password}
where no=${no }


update account
set name=#{name },
    phone_number=#{phoneNumber}
where id=${id }
