mapper.select:

select id, email, name, gender
	from account
	where id = #{id}

mapper.update:

update account
set nickname=#{nickname }
where no=${no }


update account
set password=#{password}
where no=${no }


update account
set name=#{name },
    phone_number=#{phoneNumber}
where id=${id }

mapper.select:

select email, password
	from account 
	where name = #{name} 
	and email = #{email}


select email
	from account 
	where name = #{name} 
	and phone_number = #{phoneNumber}

mapper.update:

update account
    set password=#{randomPassword }
    where email=#{email }

mapper.select:

select no
	from account 
	where name = #{name} 
	and phone_number = #{phoneNumber}
