mapper.select:

select id, name, role
	from account 
	where email = #{e} 
	and password = #{p}


select id, email, name, gender
	from account
	where id = #{id}


select id, email, name, gender
	from account
	where email = #{email}

choose.when:

	update account
       set name=#{name },
           gender=#{gender }
     where id=${id }

choose.otherwise:

update account
   set name=#{name },
       password=#{password }, 
       gender=#{gender }
 where id=${id }
